      "&lt;b&gt;Macro region : &lt;/b&gt;" + Layer.feature.properties.macro + "&lt;br&gt;" +
      Layer.feature.properties.X0001_met_respID_aut + ' - "' + Layer.feature.properties.ID_geom + '" &lt;br&gt;';
  })
    layer.on({
        mouseover: highlightFeature,
        mouseout: resetHighlight,
